import React, {useState} from 'react';

// import { Container } from './styles';

import api from '../../services/api.js'
export default function MessageInput() {


  const [message, setMessage] = useState("");

async function sendMessage (e) {
    e.preventDefault();
    console.log('The link was clicked.');
   await  api.post('/comments', { name: 'Marlon', text: 'Bernardes' }, config)

  }

  const config = {
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    }
  }
  function handleChange(event) {
    setMessage(event.target.value)
  }

  return (
    <footer className="teal">
  <form className="container">
    <div className="row">
      <div className="input-field col s10">
        <i className="prefix mdi-communication-chat" />
        <input type="text" value={message} onChange={handleChange} placeholder="Type your message" />
        <span className="chip left">

          <span>Anonymous robot #503483</span>
        </span>
      </div>
      <div className="input-field col s2">
        <button type="submit" onClick={sendMessage}className="waves-effect waves-light btn-floating btn-large">
          <i className="mdi-content-send" />
        </button>
      </div>
    </div>
  </form>
</footer>
)
}
