import React, {useState} from 'react';

// import { Container } from './styles';
import axios from 'axios';
import qs from 'qs'
import api from '../../services/api.js'
export default function MessageInput() {


  const [message, setMessage] = useState("");

    async function sendMessage (e) {
    e.preventDefault();
    console.log('The link was clicked.');
   await  axios.post('http://localhost:3001/comment', { name: 'Marlon', text: 'Bernardes' }, config)

   axios({
    method: 'post',
    url: 'http://localhost:3001/comment',
    data: qs.stringify({
      item1: 'value1',
      item2: 'value2'
    }),
    headers: {
      'content-type': 'application/x-www-form-urlencoded;charset=utf-8'
    }
  })

  }

  const config = {
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    }
  }
  function handleChange(event) {
    setMessage(event.target.value)
  }

  return (
    <footer className="teal">
  <form className="container">
    <div className="row">
      <div className="input-field col s10">
        <i className="prefix mdi-communication-chat" />
        <input type="text" value={message} onChange={handleChange} placeholder="Type your message" />
        <span className="chip left">

          <span>Anonymous robot #503483</span>
        </span>
      </div>
      <div className="input-field col s2">
        <button type="submit" onClick={sendMessage}className="waves-effect waves-light btn-floating btn-large">
          <i className="mdi-content-send" />
        </button>
      </div>
    </div>
  </form>
</footer>
)
}
